import React from "react";
import Modal from "../components/Modal/Modal";
import Backdrop from "../components/Backdrop/Backdrop";
import EventList from "../components/Events/EventList";
import AuthContext from "../context/auth-context";
import Spinner from "../components/Spinner/Spinner";

import "./Events.css";

class EventsPage extends React.Component {
	static contextType = AuthContext;
	constructor(props) {
		super(props);
		this.state = {
			creating: false,
			events: [],
			isLoading: false,
			selectedEvent: null
		};
		this.titleEl = React.createRef();
		this.priceEl = React.createRef();
		this.dateEl = React.createRef();
		this.descriptionEl = React.createRef();
	}

	componentDidMount() {
		this.fetchEvents();
	}

	createEventHandler = () => {
		this.setState({ creating: true });
	};

	modalConfirmHandler = () => {
		this.setState({ creating: false });
		const title = this.titleEl.current.value;
		const price = +this.priceEl.current.value;
		const date = this.dateEl.current.value;
		const description = this.descriptionEl.current.value;

		if (
			title.trim().length === 0 ||
			price <= 0 ||
			date.trim().length === 0 ||
			description.trim().length === 0
		) {
			return;
		}

		const event = { title, price, date, description };
		console.log(event);

		const requestBody = {
			query: `
					mutation {
						createEvent(eventInput: {title: "${title}", description: "${description}", price: ${price}, date: "${date}"}) {
							_id
							title
							date
							price
						}
					}
				`
		};

		const token = this.context.token;

		fetch("http://localhost:3001/graphql", {
			method: "POST",
			body: JSON.stringify(requestBody),
			headers: {
				"Content-type": "application/json",
				Authorization: "Bearer " + token
			}
		})
			.then(res => {
				if (res.status !== 200 && res.status !== 201) {
					throw new Error("Request failed");
				}
				return res.json();
			})
			.then(resData => {
				this.setState(prevState => {
					const updatedEvents = [...prevState.events];
					updatedEvents.push({
						_id: resData.data.createEvent._id,
						title: resData.data.createEvent.title,
						description: resData.data.createEvent.description,
						date: resData.data.createEvent.date,
						price: resData.data.createEvent.price,
						creator: {
							_id: this.context.userId
						}
					});
					return { events: updatedEvents };
				});
			})
			.catch(err => {
				console.log(err);
			});
	};

	modalCancelHandler = () => {
		this.setState({ creating: false, selectedEvent: null });
	};

	fetchEvents() {
		this.setState({ isLoading: true });
		const requestBody = {
			query: `
					query {
						events {
							_id
							title
							date
							price
							creator {
								_id
								email
							}
						}
					}
				`
		};

		fetch("http://localhost:3001/graphql", {
			method: "POST",
			body: JSON.stringify(requestBody),
			headers: {
				"Content-type": "application/json"
			}
		})
			.then(res => {
				if (res.status !== 200 && res.status !== 201) {
					throw new Error("Request failed");
				}
				return res.json();
			})
			.then(resData => {
				const events = resData.data.events;
				this.setState({ events: events, isLoading: false });
			})
			.catch(err => {
				console.log(err);
				this.setState({ isLoading: false });
			});
	}

	showDetailHandler = eventId => {
		this.setState(prevState => {
			const selectedEvent = prevState.events.find(e => e._id === eventId);
			return { selectedEvent: selectedEvent };
		});
	};

	bookEventHandler = () => {};

	render() {
		return (
			<React.Fragment>
				{this.state.creating && <Backdrop />}
				{this.state.creating && (
					<Modal
						title="Add Event"
						canCancel
						canConfirm
						onCancel={this.modalCancelHandler}
						onConfirm={this.modalConfirmHandler}
					>
						<form>
							<div className="form-control">
								<label htmlFor="title">Title</label>
								<input type="text" id="title" ref={this.titleEl} />
							</div>
							<div className="form-control">
								<label htmlFor="price">Price</label>
								<input type="number" id="price" ref={this.priceEl} />
							</div>
							<div className="form-control">
								<label htmlFor="date">Date</label>
								<input type="datetime-local" id="date" ref={this.dateEl} />
							</div>
							<div className="form-control">
								<label htmlFor="description">Description</label>
								<textarea id="description" rows="4" ref={this.descriptionEl} />
							</div>
						</form>
					</Modal>
				)}
				{this.state.selectedEvent && (
					<Modal
						title={this.state.selectedEvent.title}
						canCancel
						canConfirm
						onCancel={this.modalCancelHandler}
						onConfirm={this.bookEventHandler}
					>
						<h1>{this.state.selectedEvent.title}</h1>
						<h2>
							${this.state.selectedEvent.price} -{" "}
							{new Date(this.state.selectedEvent.date).toLocaleDateString()}
						</h2>
						<p>{this.state.selectedEvent.description}</p>
					</Modal>
				)}
				{this.context.token && (
					<div className="events-control">
						<p>Share your own Events!</p>
						<button className="btn" onClick={this.createEventHandler}>
							Create Event
						</button>
					</div>
				)}
				{this.state.isLoading ? (
					<Spinner />
				) : (
					<EventList
						events={this.state.events}
						userId={this.context.userId}
						viewDetails={this.showDetailHandler}
					/>
				)}
			</React.Fragment>
		);
	}
}

export default EventsPage;
